import React, { useState } from 'react';

function NavBar() {
    const [write, setWrite] = useState(false);
    const [text, setText] = useState('');
    const [ht, setHt] = useState('');
    const [chan, setChan] = useState('');

    const handleWrite = () => {
        setWrite(true);
    }
    const handleCancel = () => {
        setWrite(false);
    }

    const postMessage = () => {
        fetch('https://localhost:8080/publish', {
        method: 'POST',
        headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json',
        },
        body: JSON.stringify({
            messageText: text,
            messageColor: 'green',
            messageChannel: chan,
            messageHashtag: ht,
            messageLocation: 'Helsinki',
            messageLikes: 0
        })
        })
    }

    if (write === true) {
        return(
            <div style={{height: 400, backgroundColor: "#f26d50", textAlign: 'center'}}>
                <h1>Kirjoita</h1>
                <div style={{display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center'}}>
                    <p>Kanava:</p>
                    <select onChange={value => setChan(value)} style={{display: 'flex', width: 150}}>
                        <option value='@main'>@main</option>
                        <option value='@opiskelu'>@opiskelu</option>
                        <option value='@menot'>@menot</option>
                        <option value='@ruoka'>@ruoka</option>
                    </select>
                    <p>Teksti:</p>
                    <input 
                    onChange={text => setText(text)}
                    style={{display: 'flex', height: 100, width: 350, fontSize: 18}}></input>
                    <p>Hashtag:</p>
                    <input 
                    onChange={tag => setHt(tag)} 
                    style={{display: 'flex', width: 150}} value="#"></input>
                </div>

                <button onClick={handleCancel}>takaisin</button>
                <button onClick={postMessage}>Lähetä</button>
            </div>
        )
    }

    return(
        <div style={{display: 'flex'}}>
            <div id='home' style={{flex: 1, height: 100, backgroundColor: 'orange', textAlign: 'center'}}>
                <button style={{padding: 20}}>home</button>
            </div>
            <div id='jodels' style={{flex: 1, height: 100, backgroundColor: 'red', textAlign: 'center'}}>
                <button style={{padding: 20}} onClick={handleWrite}>POST</button>
            </div>
            <div id='channels' style={{flex: 1, height: 100, backgroundColor: 'yellow', textAlign: 'center'}}>
                <button style={{padding: 20}}>home</button>
            </div>
        </div>
    )
}

export default NavBar;
