import React, { useState, useEffect } from 'react';

function PublishBtn() {
    const [write, setWrite] = useState(false);
    const [msgObj, setMsgObj] = useState({
            messageText: '',
            messageColor: 'green',
            messageHashtag: '',
            messageLocation: 'Helsinki',
            messageLikes: 0,
            messageChannel: {
                channelId: selChannelId
            }
    });
    const [channels, setChannels] = useState([]);
    const [selChannelId, setChanId] = useState(null);

    async function fetchChannels() {
        const response = await fetch('http://localhost:8080/channels');
        const json = await response.json();
        setChannels(json);
    }

    const handleWrite = () => {
        setWrite(true);
    }
    const handleCancel = () => {
        setWrite(false);
    }

    const printObj = () => {
        console.log(msgObj);
    }
    const changeValues = (val) => {
        setChanId(val.target.value);
        setMsgObj({
            ...msgObj,
            [val.target.name]: val.target.value
        });
        console.log(selChannelId);
    }

    const clearValues = () => {
        setMsgObj({
            messageText: '',
            messageColor: 'green',
            messageHashtag: '',
            messageLocation: 'Helsinki',
            messageLikes: 0,
            messageChannel: {
                channelId: ''
            }
        });
    }

    const postMessage = () => {
        fetch('http://localhost:8080/publish', {
        method: 'POST',
        headers: {'Accept': 'application/json', 'Content-Type': 'application/json'},
        body: JSON.stringify(msgObj)
        });
        console.log("Message posted!");
    }

    useEffect(() => {fetchChannels()}, []);


    /* CONDITIONAL RENDERING */

    if (write === true) {
        return(
            <div style={{height: 400, backgroundColor: "#f26d50", textAlign: 'center'}}>
                <h1>Publish Message</h1>
                <div style={{display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center'}}>
                    <p>Channel:</p>
                    <select name='messageChannel' value={msgObj.messageChannel.channelId} onChange={changeValues} style={{display: 'flex', width: 150}}>
                    {channels.map(chan =>
                       <option 
                       key={chan.channelId} 
                       value={chan.channelId}
                       >{chan.channelName}</option> 
                    )}
                    </select>
                    <p>Text:</p>
                    <input name='messageText' value={msgObj.messageText}
                    onChange={changeValues}
                    style={{display: 'flex', height: 100, width: 350, fontSize: 18}}></input>
                    <p>Hashtag:</p>
                    <input name='messageHashtag' value={msgObj.messageHashtag} 
                    onChange={changeValues} 
                    style={{display: 'flex', width: 150}}></input>
                </div>

                <button onClick={handleCancel}>takaisin</button>
                <button onClick={postMessage}>Lähetä</button>
                <button onClick={printObj}>LOG</button>
                <button onClick={clearValues}>Tyhjennä</button>
            </div>
        )
    }

    return(
           <div style={{display: 'flex', alignItems: 'center', justifyContent: 'center', opacity: 0.7}}>
            <button 
            style={{
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: 100, 
                borderRadius: 100, 
                height: 200, 
                width: 200, 
                borderWidth: 4,
                paddingBottom: 20,
                borderColor: 'white', 
                color: 'grey'}} 
                onClick={handleWrite}>+</button>
            </div>
    )
}

export default PublishBtn;
