import React, { useState, useEffect } from 'react';

function PublishBtn() {
    const [write, setWrite] = useState(false);
    const [selChannelId, setChanId] = useState(1);
    const [msgObj, setMsgObj] = useState({
            messageText: '',
            messageColor: 'green',
            messageHashtag: '',
            messageLocation: 'Helsinki',
            messageLikes: 0,
            messageChannel: {
                channelId: selChannelId
            }
    });
    const [text, setText] = useState('');
    const [ht, setHt] = useState('#');
    const [loc, setLoc] = useState('Helsinki');
    const [color, setColor] = useState('#f26d50');

    const [channels, setChannels] = useState([]);


    async function fetchChannels() {
        const response = await fetch('http://localhost:8080/channels');
        const json = await response.json();
        setChannels(json);
    }

    const handleWrite = () => {
        setWrite(true);
    }
    const handleCancel = () => {
        setWrite(false);
    }

    const printObj = () => {
        console.log(msgObj);
        console.log(loc, text, ht, selChannelId, color);
    }
    const changeValues = (val) => {
        const name = val.target.name;
        const value = val.target.value;
        setChanId(val.target.value);
        setMsgObj({
            ...msgObj,
            [name]: value
        });
    }

    const clearValues = () => {
        setMsgObj({
            messageText: '',
            messageColor: 'green',
            messageHashtag: '',
            messageLocation: 'Helsinki',
            messageLikes: 0,
            messageChannel: {
                channelId: ''
            }
        });
    }

    const postMessage = () => {
        fetch('http://localhost:8080/publish', {
        method: 'POST',
        headers: {'Accept': 'application/json', 'Content-Type': 'application/json'},
        body: JSON.stringify(msgObj)
        });
        console.log("Message posted!");
    }

    useEffect(() => {fetchChannels()}, []);


    /* CONDITIONAL RENDERING */

    if (write === true) {
        return(
            <div style={{height: 400, backgroundColor: "#f26d50", textAlign: 'center'}}>
                <h1>Publish Message</h1>
                <div style={{display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center'}}>
                    <p>Channel:</p>
                    <select name='messageChannel' value={selChannelId} onChange={(e) => setChanId(e.target.value)} style={{display: 'flex', width: 150}}>
                    {channels.map(chan =>
                       <option 
                       key={chan.channelId} 
                       value={chan.channelId}
                       >{chan.channelName}</option> 
                    )}
                    </select>
                    <p>Location:</p>
                    <select name='messageLocation' value={loc} onChange={(e) => setLoc(e.target.value)} style={{display: 'flex', width: 150}}>
                        <option value='Helsinki'>Helsinki</option>
                        <option value='Stockholm'>Stockholm</option>
                        <option value='Copenhagen'>Copenhage</option>
                        <option value='Madrid'>Madrid</option>
                    </select>
                    <p>Text:</p>
                    <input name='messageText' value={text}
                    onChange={(e) => setText(e.target.value)}
                    style={{display: 'flex', height: 100, width: 350, fontSize: 18}}></input>
                    <p>Hashtag:</p>
                    <input name='messageHashtag' value={ht} 
                    onChange={(e) => setHt(e.target.value)} 
                    style={{display: 'flex', width: 150}}></input>
                    <p>Color:</p>
                    <select name='messageColor' value={color} onChange={(e) => setColor(e.target.value)}>
                        <option value='#f26d50'>Tomato Red</option>
                        <option value='#0f8518'>Cucumber Green</option>
                        <option value='#ffc61c'>Sunrise Yellow</option>
                        <option value='#3e9cbf'>Skyline Blue</option>
                        <option value='#f5aa5f'>Chatty Orange</option>
                    </select>
                    <div className='palette'>
                    </div>
                </div>

                <button onClick={handleCancel}>takaisin</button>
                <button onClick={postMessage}>Lähetä</button>
                <button onClick={printObj}>LOG</button>
                <button onClick={clearValues}>Tyhjennä</button>
            </div>
        )
    }

    return(
           <div style={{display: 'flex', alignItems: 'center', justifyContent: 'center', opacity: 0.7}}>
            <button 
            style={{
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: 100, 
                borderRadius: 100, 
                height: 200, 
                width: 200, 
                borderWidth: 4,
                paddingBottom: 20,
                borderColor: 'white', 
                color: 'grey'}} 
                onClick={handleWrite}>+</button>
            </div>
    )
}

export default PublishBtn;
